mapper.insert:

				select 
					alarm_num.nextval alarm_id
				from dual
		
	insert into alarm(
				alarm_id
				,contents
				,alarm_code
				,page_type
				,receiver
				,sender
				,read_yn
				,reg_date
				,board_id
			)
				values(
				#{alarm_id}
				,#{contents}
				,#{alarm_code}
				,#{page_type}
				,#{receiver}
				,#{sender}
				,#{read_yn}
				,sysdate
				,#{board_id}
			)


