mapper.insert:
insert into alarm(
			alarm_id
			,contents
			,alarm_code
			,page_type
			,receiver
			,sender
			,read_yn
			,reg_date
		)
			values(
			alarm_num.nextval
			,#{contents}
			,#{alarm_code}
			,#{page_type}
			,#{receiver}
			,#{sender}
			,#{read_yn}
			,sysdate
		)

mapper.update:
update alarm
set read_yn = 'Y'
where ALARM_ID=#{alarm_id}
