mapper.select:
SELECT p.board_id, p.title, t.low_name AS givetalent, t2.low_name AS receivetalent, p.writer_nickname, p.read_count, p.register_date, s.STATUS_CONTENTS AS status, u.city_name AS cityname, u.district_name AS districtname FROM ( SELECT row_sub.* FROM ( SELECT rownum as page_num, sub.* FROM ( SELECT * FROM board b WHERE delete_yn = 'N'
AND title LIKE '%' || #{searchWord} || '%'
AND board_id in ( SELECT DISTINCT talent.board_id FROM ( SELECT * FROM board b WHERE delete_yn = 'N' AND give_talent IN (SELECT low_code FROM talent_small WHERE low_name LIKE '%' || #{searchWord} || '%') UNION ALL SELECT * FROM board b WHERE delete_yn = 'N' AND receive_talent IN (SELECT low_code FROM talent_small WHERE low_name LIKE '%' || #{searchWord} || '%')
) talent) ORDER BY read_count DESC, board_id DESC ) sub
where rownum <= ${endnum}
) row_sub WHERE page_num BETWEEN ${startnum} and ${endnum}) p INNER JOIN talent_small t ON p.receive_talent = t.low_code INNER JOIN talent_small t2 ON p.give_talent = t2.low_code INNER JOIN status s ON p.status_code = s.STATUS_CODE INNER JOIN ( SELECT c.city_code, d.city_code AS district_city_code, c.city_name, d.district_name, d.district_code FROM city c INNER JOIN district d ON c.city_code = d.city_code) u ON p.city_code = u.city_code AND p.district_code = u.district_code

	select b.*
	from (
	        select rownum nrow, a.*
	        from (
	                select board_id
	                		,title
	                        ,contents
	                        ,city_name
	                        ,district_name
	                        ,ts1.low_name give_talent
	                        , ts2.low_name receive_talent

	                from board join district using(district_code, city_code) join city using(city_code)
	                        join talent_small ts1 on board.give_talent = ts1.low_code
	                        join talent_small ts2 on board.receive_talent = ts2.low_code


	                	delete_yn = 'N'

						    AND city_code = #{city_code}


						    AND district_code = #{district_code}


						    AND board.receive_talent = #{low_code}


  						    AND ts2.high_code = #{high_code}


	                order by register_date desc
	                ) a

	        where rownum <= #{endNo}) b
	    where nrow between #{startNo} and #{endNo}

select count(*) 
 from board join district using(district_code,city_code)join city using(city_code)
	                        join talent_small ts1 on board.give_talent=ts1.low_code
	                        join talent_small ts2 on board.receive_talent=ts2.low_code

	                delete_yn = 'N'

						    AND city_code = #{city_code}


						    AND district_code = #{district_code}


						    AND board.receive_talent = #{low_code}


  						    AND ts2.high_code = #{high_code}

mapper.update:
update board
set delete_yn='Y'
where board_id = #{board_id}

mapper.select:
select district_code, district_name
from district

	where city_code = #{city_code}

select low_code, low_name
from talent_small

	where high_code=#{high_code}

mapper.update:
update board
set read_count = read_count + 1
where board_id = ${board_id}

mapper.insert:

			select 
				board_num.nextval board_id 
			from dual

insert into board(
			board_id
			,title
			,contents
			,writer_nickname
			,register_date
			,status_code
			,district_code
			,city_code
			,give_talent
			,receive_talent
			,high_code

			)values(
				#{board_id}
				,#{title}
				,#{contents}
				,#{writer_nickname}
				,sysdate
				,'SC01'
				,#{district_code}
				,#{city_code}
				,#{give_talent}
				,#{receive_talent}
				,#{high_code}

			)

mapper.update:
update board
set status_code ='SC02'
where board_id = #{board_id}

mapper.select:
select BOARD.BOARD_ID, BOARD.CONTENTS, BOARD.READ_COUNT, TO_CHAR(BOARD.REGISTER_DATE, 'YYYY"-"MM"-"DD' ) AS register_date, GIVE_TALENT.LOW_NAME as giveTalentLowName, RECEIVE_TALENT.LOW_NAME as receiveTalentLowName, u.CITY_NAME AS city_name, u.DISTRICT_NAME AS district_name from ( select BOARD_ID, BOARD.CONTENTS, READ_COUNT, REGISTER_DATE, GIVE_TALENT, RECEIVE_TALENT, CITY_CODE, DISTRICT_CODE from BOARD where BOARD.GIVE_TALENT= #{give_talent}
AND rownum <= 6
order by READ_COUNT DESC ) BOARD JOIN TALENT_SMALL GIVE_TALENT ON GIVE_TALENT.LOW_CODE = BOARD.GIVE_TALENT JOIN TALENT_SMALL RECEIVE_TALENT ON RECEIVE_TALENT.LOW_CODE = BOARD.RECEIVE_TALENT JOIN ( SELECT c.city_code, d.city_code AS district_city_code, c.city_name, d.district_name, d.district_code FROM CITY c JOIN DISTRICT d ON c.city_code = d.city_code) u ON BOARD.city_code = u.city_code AND BOARD.district_code = u.district_code order by READ_COUNT desc
