mapper.update:
update chat_room
set connected_count = connected_count + 1
where room_id = #{room_id}

update chat_room
set connected_count = connected_count - 1
where room_id = #{room_id}

mapper.select:
select *
from chat_room
where board_id = #{board_id} 
		and receiver =#{receiver} 
		and sender =#{sender}
