mapper.insert:
insert into message(
				message_id
				,message
				,read_yn
				,reg_date
				,sender
				,room_id
			) values(
				message_num.nextval
				,#{message}
				,#{read_yn}
				,#{reg_date}
				,#{sender}
				,#{room_id}
			)

mapper.update:
update message
set read_yn='Y'
where room_id=#{room_id}
